// FWAI Conversational Flow - Enhanced State Machine with 12 Discovery Questions
// Based on analysis of real sales call patterns

// Handle both direct JSON and body-wrapped JSON from webhook
const rawData = $input.first().json;
const data = rawData.body || rawData;
const text = (data.text || '').toLowerCase();
const callId = data.call_uuid;

// Get current state from static data
const staticData = $getWorkflowStaticData('global');
const callStates = staticData.callStates || {};
const currentState = callStates[callId] || { 
  phase: 'opening', 
  data: {},
  turn_count: 0,
  sentiment: 'neutral',
  domain: null,
  objections_handled: [],
  discovery_completed: []
};

currentState.turn_count++;
let nextPhase = null;
let capturedData = currentState.data || {};
let contextToInject = '';

// ============ DOMAIN DETECTION ============
function detectDomain(text) {
  const domains = {
    'finance': ['finance', 'fp&a', 'accounting', 'banking', 'investment', 'financial', 'analyst', 'accounts', 'ca ', 'chartered'],
    'it': ['developer', 'software', 'engineer', 'programming', 'coding', 'python', 'java', 'frontend', 'backend', 'fullstack', 'data science', 'machine learning', 'devops', 'bca', 'mca', 'btech', 'cse'],
    'marketing': ['marketing', 'digital marketing', 'seo', 'social media', 'content', 'brand', 'advertising', 'ads'],
    'student': ['student', 'studying', 'college', 'university', 'degree', 'pursuing', 'final year', 'fresher', 'graduate'],
    'education': ['teacher', 'professor', 'lecturer', 'teaching', 'b.ed', 'bed', 'education']
  };

  for (const [domain, keywords] of Object.entries(domains)) {
    for (const keyword of keywords) {
      if (text.includes(keyword)) {
        return domain;
      }
    }
  }
  return null;
}

// ============ DOMAIN-SPECIFIC TOOLS ============
const domainTools = {
  'finance': ['DataRails', 'Pigment', 'Planful', 'Cube Software', 'Vena'],
  'it': ['GitHub Copilot', 'Cursor', 'Claude', 'LangChain', 'OpenAI API'],
  'marketing': ['Canva AI', 'InVideo', 'Jasper', 'Copy.ai', 'AdCreative.ai'],
  'student': ['ChatGPT', 'Claude', 'Notion AI', 'Grammarly'],
  'education': ['Canva for Education', 'Quizlet AI', 'Notion AI']
};

// ============ EXPERIENCE EXTRACTION ============
function extractExperience(text) {
  const patterns = [
    /(\d+)\s*(?:years?|yrs?)\s*(?:of)?\s*(?:experience)?/i,
    /(?:experience|exp)\s*(?:of)?\s*(\d+)\s*(?:years?|yrs?)/i,
    /(?:working|worked)\s*(?:for)?\s*(\d+)\s*(?:years?|yrs?)/i,
    /(\d+)\s*(?:months?)\s*(?:experience)?/i
  ];

  for (const pattern of patterns) {
    const match = text.match(pattern);
    if (match) {
      const num = parseInt(match[1]);
      if (text.includes('month')) {
        return { years: Math.round(num / 12 * 10) / 10, raw: text };
      }
      return { years: num, raw: text };
    }
  }
  return null;
}

// ============ AI RATING EXTRACTION ============
function extractAIRating(text) {
  const patterns = [
    /(\d+)\s*(?:out of|\/)\s*10/i,
    /(?:rating|rate|score)\s*(?:is)?\s*(\d+)/i,
    /^\s*(\d+)\s*$/
  ];
  for (const pattern of patterns) {
    const match = text.match(pattern);
    if (match) {
      const num = parseInt(match[1]);
      if (num >= 1 && num <= 10) return num;
    }
  }
  // Check for word ratings
  if (text.includes('beginner') || text.includes('basic')) return 3;
  if (text.includes('intermediate') || text.includes('moderate')) return 5;
  if (text.includes('advanced') || text.includes('expert')) return 8;
  return null;
}

// ============ SENTIMENT TRACKING ============
function analyzeSentiment(text, currentSentiment) {
  const positive = ['yes', 'great', 'good', 'interested', 'sure', 'okay', 'sounds good', 'amazing', 'love', 'excited', 'definitely', 'absolutely'];
  const negative = ['no', 'not interested', 'busy', 'later', 'bye', 'expensive', 'can\'t', 'won\'t', 'difficult'];
  const hesitant = ['maybe', 'think', 'not sure', 'let me', 'need to', 'have to ask', 'discuss', 'decide'];

  let score = currentSentiment === 'positive' ? 1 : (currentSentiment === 'negative' ? -1 : 0);

  for (const word of positive) {
    if (text.includes(word)) score += 0.5;
  }
  for (const word of negative) {
    if (text.includes(word)) score -= 0.5;
  }
  for (const word of hesitant) {
    if (text.includes(word)) score -= 0.2;
  }

  if (score > 0.5) return 'positive';
  if (score < -0.5) return 'negative';
  return 'neutral';
}

// Update sentiment
currentState.sentiment = analyzeSentiment(text, currentState.sentiment);

// ============ OBJECTION DETECTION (Priority) ============
// Check objections FIRST as they can happen at any phase

if ((text.includes('father') || text.includes('mother') || text.includes('parent') || text.includes('family') || text.includes('husband') || text.includes('wife') || text.includes('sister') || text.includes('brother')) && 
    (text.includes('ask') || text.includes('discuss') || text.includes('talk') || text.includes('tell'))) {
  if (!currentState.objections_handled.includes('family')) {
    nextPhase = 'objection_family';
    currentState.objections_handled.push('family');
  }
}
else if (text.includes('exam') || text.includes('test') || text.includes('semester')) {
  if (!currentState.objections_handled.includes('exams')) {
    nextPhase = 'objection_exams';
    capturedData.has_exams = true;
    const dateMatch = text.match(/(\d{1,2})(?:st|nd|rd|th)?\s*(?:of)?\s*(january|february|march|april|may|june|july|august|september|october|november|december|jan|feb|mar|apr|may|jun|jul|aug|sep|oct|nov|dec)/i);
    if (dateMatch) capturedData.exam_end_date = dateMatch[0];
    currentState.objections_handled.push('exams');
  }
}
else if ((text.includes('time') || text.includes('decide') || text.includes('think')) && 
         (text.includes('give me') || text.includes('need') || text.includes('let me') || text.includes('some'))) {
  if (!currentState.objections_handled.includes('time')) {
    nextPhase = 'objection_need_time';
    currentState.objections_handled.push('time');
  }
}
else if (text.includes('call') && (text.includes('later') || text.includes('back') || text.includes('tomorrow') || text.includes('monday') || text.includes('evening') || text.includes('morning'))) {
  nextPhase = 'objection_callback';
  if (text.includes('evening')) capturedData.callback_time = 'evening';
  else if (text.includes('morning')) capturedData.callback_time = 'morning';
  else if (text.includes('tomorrow')) capturedData.callback_time = 'tomorrow';
  else if (text.includes('monday')) capturedData.callback_time = 'monday';
  capturedData.needs_callback = true;
}
else if (text.includes('live') && (text.includes('class') || text.includes('session')) && (text.includes('prefer') || text.includes('want') || text.includes('better'))) {
  if (!currentState.objections_handled.includes('live_class')) {
    nextPhase = 'objection_live_class';
    currentState.objections_handled.push('live_class');
  }
}
else if (text.includes('price') || text.includes('cost') || text.includes('expensive') || text.includes('afford') || text.includes('budget') || (text.includes('how much') && text.includes('fee'))) {
  if (!currentState.objections_handled.includes('price')) {
    nextPhase = 'objection_price';
    currentState.objections_handled.push('price');
  }
}
else if (text.includes('youtube') || text.includes('free') || (text.includes('online') && text.includes('learn'))) {
  if (!currentState.objections_handled.includes('youtube')) {
    nextPhase = 'objection_youtube';
    currentState.objections_handled.push('youtube');
  }
}
else if (text.includes('not interested') || text.includes('no thanks') || text.includes('don\'t want')) {
  nextPhase = 'objection_not_interested';
}

// ============ PHASE: OPENING (Q1 - Why registered) ============
if (!nextPhase && currentState.phase === 'opening') {
  const domain = detectDomain(text);
  if (domain) {
    currentState.domain = domain;
    capturedData.domain = domain;
  }

  // Capture registration reason (Q1)
  capturedData.registration_reason = text;

  if (text.includes('good') || text.includes('great') || text.includes('loved') || text.includes('amazing') || text.includes('helpful') || text.includes('excellent') || text.includes('nice')) {
    nextPhase = 'connection_liked';
  } else if (text.includes('okay') || text.includes('fine') || text.includes('alright') || text.includes('decent')) {
    nextPhase = 'connection_neutral';
  } else if (text.includes('not good') || text.includes('bad') || text.includes("didn't like") || text.includes("don't remember") || text.includes('boring') || text.includes('waste')) {
    nextPhase = 'connection_dislike';
  } else {
    nextPhase = 'connection_probe';
  }
}

// ============ PHASE: CONNECTION RESPONSE ============
else if (!nextPhase && currentState.phase.startsWith('connection_')) {
  const domain = detectDomain(text);
  if (domain && !currentState.domain) {
    currentState.domain = domain;
    capturedData.domain = domain;
  }
  // Move to Q2: Expectations
  nextPhase = 'discovery_expectations';
}

// ============ DISCOVERY PHASES (12 Key Questions) ============

// Q2: Expectations after masterclass
else if (!nextPhase && currentState.phase === 'discovery_expectations') {
  capturedData.expectations = text;
  currentState.discovery_completed.push('expectations');
  // Move to Q3: How they heard about FWAI
  nextPhase = 'discovery_source';
}

// Q3: How they heard about FWAI/Avinash Mada
else if (!nextPhase && currentState.phase === 'discovery_source') {
  capturedData.lead_source = text;
  // Detect specific sources
  if (text.includes('youtube')) capturedData.lead_source_type = 'youtube';
  else if (text.includes('instagram') || text.includes('insta')) capturedData.lead_source_type = 'instagram';
  else if (text.includes('facebook') || text.includes('fb')) capturedData.lead_source_type = 'facebook';
  else if (text.includes('linkedin')) capturedData.lead_source_type = 'linkedin';
  else if (text.includes('friend') || text.includes('colleague') || text.includes('referred')) capturedData.lead_source_type = 'referral';
  else if (text.includes('ad') || text.includes('advertisement')) capturedData.lead_source_type = 'ads';
  else if (text.includes('google') || text.includes('search')) capturedData.lead_source_type = 'organic_search';
  else capturedData.lead_source_type = 'other';
  currentState.discovery_completed.push('source');
  // Move to Q4: What they know about FWAI
  nextPhase = 'discovery_knowledge_fwai';
}

// Q4: What they know about FWAI
else if (!nextPhase && currentState.phase === 'discovery_knowledge_fwai') {
  capturedData.fwai_knowledge = text;
  // Check if they know about FWAI
  if (text.includes('don\'t know') || text.includes('not sure') || text.includes('no idea') || text.length < 20) {
    capturedData.fwai_awareness = 'low';
  } else if (text.includes('ai') || text.includes('course') || text.includes('training')) {
    capturedData.fwai_awareness = 'medium';
  } else {
    capturedData.fwai_awareness = 'high';
  }
  currentState.discovery_completed.push('fwai_knowledge');
  // Move to Q5: AI understanding
  nextPhase = 'discovery_ai_understanding';
}

// Q5: General AI understanding
else if (!nextPhase && currentState.phase === 'discovery_ai_understanding') {
  capturedData.ai_understanding = text;
  currentState.discovery_completed.push('ai_understanding');
  // Move to Q6: AI rating
  nextPhase = 'discovery_ai_rating';
}

// Q6: Self-rating on AI (1-10)
else if (!nextPhase && currentState.phase === 'discovery_ai_rating') {
  const rating = extractAIRating(text);
  if (rating) {
    capturedData.ai_knowledge_rating = rating;
  } else {
    capturedData.ai_knowledge_rating_raw = text;
  }
  currentState.discovery_completed.push('ai_rating');
  // Move to Q7: AI usage at work
  nextPhase = 'situation_ai_usage';
}

// Q7: AI usage (existing phase - situation_ai_usage)
else if (!nextPhase && currentState.phase === 'situation_ai_usage') {
  capturedData.uses_ai = text.includes('yes') || text.includes('using') || text.includes('chatgpt') || text.includes('gemini') || text.includes('claude') || text.includes('copilot');
  if (capturedData.uses_ai) {
    capturedData.ai_usage_description = text;
  }
  currentState.discovery_completed.push('ai_usage');
  // Move to Q8: AI importance
  nextPhase = 'discovery_ai_importance';
}

// Q8: Importance of learning AI
else if (!nextPhase && currentState.phase === 'discovery_ai_importance') {
  capturedData.ai_importance = text;
  if (text.includes('yes') || text.includes('important') || text.includes('essential') || text.includes('definitely') || text.includes('must')) {
    capturedData.sees_ai_as_important = true;
    // Check their plan
    if (text.includes('self') || text.includes('youtube') || text.includes('online')) {
      capturedData.learning_plan = 'self_study';
    } else if (text.includes('course') || text.includes('training') || text.includes('mentor') || text.includes('guided')) {
      capturedData.learning_plan = 'guided';
    } else {
      capturedData.learning_plan = 'undecided';
    }
  } else {
    capturedData.sees_ai_as_important = false;
  }
  currentState.discovery_completed.push('ai_importance');
  // Move to Q9: Desired AI result
  nextPhase = 'discovery_ai_result';
}

// Q9: What result do they want from AI in 3-6 months
else if (!nextPhase && currentState.phase === 'discovery_ai_result') {
  capturedData.desired_ai_result = text;
  // Categorize the desired result
  if (text.includes('job') || text.includes('placement') || text.includes('hire')) {
    capturedData.primary_goal = 'job';
  } else if (text.includes('income') || text.includes('money') || text.includes('earn') || text.includes('freelance') || text.includes('side hustle')) {
    capturedData.primary_goal = 'passive_income';
  } else if (text.includes('promotion') || text.includes('hike') || text.includes('salary')) {
    capturedData.primary_goal = 'career_growth';
  } else if (text.includes('business') || text.includes('startup') || text.includes('own company')) {
    capturedData.primary_goal = 'entrepreneurship';
  } else if (text.includes('efficient') || text.includes('automate') || text.includes('productivity')) {
    capturedData.primary_goal = 'productivity';
  } else {
    capturedData.primary_goal = 'upskilling';
  }
  currentState.discovery_completed.push('ai_result');
  // Move to Q10: Current work
  nextPhase = 'situation_role';
}

// Q10: Current work (existing phase - situation_role)
else if (!nextPhase && currentState.phase === 'situation_role') {
  capturedData.role_description = text;

  const domain = detectDomain(text);
  if (domain) {
    currentState.domain = domain;
    capturedData.domain = domain;
  }

  if (text.includes('student') || text.includes('studying') || text.includes('pursuing') || text.includes('college') || text.includes('university')) {
    capturedData.profile_type = 'student';
    nextPhase = 'situation_student_year';
  } else if (text.includes('working') || text.includes('job') || text.includes('company') || text.includes('employed')) {
    capturedData.profile_type = 'working';
    nextPhase = 'situation_experience';
  } else {
    const exp = extractExperience(text);
    if (exp) {
      capturedData.experience_years = exp.years;
      capturedData.profile_type = 'working';
      // Move to Q11: Long-term goal
      nextPhase = 'discovery_long_term_goal';
    } else {
      nextPhase = 'situation_experience';
    }
  }
  currentState.discovery_completed.push('current_work');
}

else if (!nextPhase && currentState.phase === 'situation_student_year') {
  capturedData.student_year = text;
  // Move to Q11: Long-term goal
  nextPhase = 'discovery_long_term_goal';
}

else if (!nextPhase && currentState.phase === 'situation_experience') {
  const exp = extractExperience(text);
  if (exp) capturedData.experience_years = exp.years;
  // Move to Q11: Long-term goal
  nextPhase = 'discovery_long_term_goal';
}

// Q11: Long-term goal
else if (!nextPhase && currentState.phase === 'discovery_long_term_goal') {
  capturedData.long_term_goal = text;
  // Categorize goal
  if (text.includes('senior') || text.includes('manager') || text.includes('lead') || text.includes('director')) {
    capturedData.long_term_goal_type = 'senior_role';
  } else if (text.includes('business') || text.includes('own') || text.includes('startup') || text.includes('entrepreneur')) {
    capturedData.long_term_goal_type = 'entrepreneurship';
  } else if (text.includes('abroad') || text.includes('foreign') || text.includes('us') || text.includes('uk') || text.includes('canada')) {
    capturedData.long_term_goal_type = 'global_career';
  } else if (text.includes('freelance') || text.includes('independent') || text.includes('consultant')) {
    capturedData.long_term_goal_type = 'freelancing';
  } else {
    capturedData.long_term_goal_type = 'career_growth';
  }
  currentState.discovery_completed.push('long_term_goal');
  // Move to Q12: Roadmap/mentor
  nextPhase = 'discovery_roadmap';
}

// Q12: Do they have a roadmap/mentor? Looking for guidance?
else if (!nextPhase && currentState.phase === 'discovery_roadmap') {
  capturedData.roadmap_response = text;
  if (text.includes('no') || text.includes('don\'t have') || text.includes('not really') || text.includes('looking for') || text.includes('need')) {
    capturedData.has_roadmap = false;
    capturedData.needs_guidance = true;
  } else if (text.includes('yes') || text.includes('have') || text.includes('following')) {
    capturedData.has_roadmap = true;
    capturedData.needs_guidance = false;
  } else {
    capturedData.has_roadmap = false;
    capturedData.needs_guidance = text.includes('guidance') || text.includes('help') || text.includes('mentor');
  }
  currentState.discovery_completed.push('roadmap');
  // Discovery complete - move to pain discovery or presentation
  if (capturedData.needs_guidance) {
    // Perfect opportunity to pitch
    nextPhase = 'presentation_overview';
  } else {
    // Still go through pain discovery
    nextPhase = capturedData.profile_type === 'student' ? 'pain_career' : 'pain_job_market';
  }
}

// ============ PHASE: PAIN POINTS ============
else if (!nextPhase && currentState.phase === 'pain_job_market') {
  capturedData.job_market_concern = text;
  nextPhase = 'pain_challenges';
}
else if (!nextPhase && currentState.phase === 'pain_career') {
  capturedData.career_concern = text;
  nextPhase = 'pain_challenges';
}
else if (!nextPhase && currentState.phase === 'pain_challenges') {
  capturedData.biggest_challenge = text;
  nextPhase = 'presentation_overview';
}

// ============ PHASE: PRESENTATION ============
else if (!nextPhase && currentState.phase === 'presentation_overview') {
  if (text.includes('?') || text.includes('what') || text.includes('how') || text.includes('which')) {
    nextPhase = 'presentation_details';
  } else if (text.includes('okay') || text.includes('got it') || text.includes('understand')) {
    nextPhase = 'presentation_benefits';
  } else {
    nextPhase = 'presentation_benefits';
  }
}
else if (!nextPhase && currentState.phase === 'presentation_details') {
  nextPhase = 'presentation_benefits';
}
else if (!nextPhase && currentState.phase === 'presentation_benefits') {
  nextPhase = qualifyLead(capturedData, currentState);
}

// ============ CLOSING PHASES ============
else if (!nextPhase && currentState.phase.startsWith('closing_')) {
  if (text.includes('interested') || text.includes('join') || text.includes('enroll') || text.includes('sign up') || text.includes('register')) {
    nextPhase = 'closing_payment';
  } else if (text.includes('think') || text.includes('decide') || text.includes('later')) {
    nextPhase = 'closing_followup';
  }
}
else if (!nextPhase && currentState.phase === 'closing_payment') {
  nextPhase = 'closing_complete';
}

// ============ OBJECTION RESPONSE PHASES ============
else if (!nextPhase && currentState.phase === 'objection_family') {
  nextPhase = 'followup_schedule';
}
else if (!nextPhase && currentState.phase === 'objection_exams') {
  nextPhase = 'presentation_flexibility';
}
else if (!nextPhase && currentState.phase === 'objection_need_time') {
  if (text.includes('today') || text.includes('evening') || text.includes('tonight')) {
    capturedData.decision_by = 'today_evening';
  } else if (text.includes('tomorrow')) {
    capturedData.decision_by = 'tomorrow';
  } else if (text.includes('monday') || text.includes('next week')) {
    capturedData.decision_by = 'next_week';
  }
  nextPhase = 'followup_schedule';
}
else if (!nextPhase && currentState.phase === 'objection_callback') {
  nextPhase = 'followup_confirm';
}
else if (!nextPhase && currentState.phase === 'objection_live_class') {
  nextPhase = currentState.data.previous_phase || 'presentation_overview';
}
else if (!nextPhase && currentState.phase === 'objection_price') {
  nextPhase = currentState.sentiment === 'positive' ? 'closing_warm' : 'followup_schedule';
}
else if (!nextPhase && currentState.phase === 'objection_youtube') {
  nextPhase = 'presentation_benefits';
}
else if (!nextPhase && currentState.phase === 'presentation_flexibility') {
  nextPhase = currentState.sentiment === 'positive' ? 'closing_warm' : 'followup_schedule';
}

// ============ FOLLOWUP PHASES ============
else if (!nextPhase && currentState.phase === 'followup_schedule') {
  if (text.includes('evening')) capturedData.callback_time = 'evening';
  else if (text.includes('morning')) capturedData.callback_time = 'morning';
  else if (text.includes('tomorrow')) capturedData.callback_time = 'tomorrow';
  else if (text.includes('noon') || text.includes('12')) capturedData.callback_time = 'tomorrow_noon';
  nextPhase = 'followup_confirm';
}
else if (!nextPhase && currentState.phase === 'followup_confirm') {
  capturedData.followup_scheduled = true;
  nextPhase = 'closing_scheduled';
}

// ============ LEAD QUALIFICATION ============
function qualifyLead(data, state) {
  const years = data.experience_years || 0;
  const isWorking = data.profile_type === 'working';
  const isStudent = data.profile_type === 'student';
  const hasPain = data.biggest_challenge || data.career_concern || data.job_market_concern;
  const sentiment = state.sentiment;
  const objectionCount = state.objections_handled.length;
  const aiRating = data.ai_knowledge_rating || 5;
  const needsGuidance = data.needs_guidance;
  const seesAIImportant = data.sees_ai_as_important;

  // HOT: Working professional 3+ years, positive sentiment, few objections, needs guidance
  if (years >= 3 && isWorking && sentiment === 'positive' && objectionCount <= 1 && (needsGuidance || seesAIImportant)) {
    return 'closing_hot';
  }
  // HOT: Student final year, very interested, needs guidance
  if (isStudent && sentiment === 'positive' && (data.primary_goal === 'job' || data.primary_goal === 'passive_income') && needsGuidance) {
    return 'closing_hot';
  }
  // HOT: Low AI rating (1-4) + sees AI as important + needs guidance
  if (aiRating <= 4 && seesAIImportant && needsGuidance) {
    return 'closing_hot';
  }
  // WARM: Working professional 1+ years OR interested student
  if ((years >= 1 && isWorking) || (isStudent && sentiment !== 'negative')) {
    return 'closing_warm';
  }
  // COLD: Just exploring or negative sentiment
  return 'closing_cold';
}

// ============ CONTEXT INJECTION ============
if (currentState.domain && domainTools[currentState.domain]) {
  contextToInject = `Relevant AI tools for ${currentState.domain}: ${domainTools[currentState.domain].join(', ')}`;
}

// Add personalization based on discovery
if (capturedData.ai_knowledge_rating) {
  if (capturedData.ai_knowledge_rating <= 3) {
    contextToInject += ' [USER HAS LOW AI KNOWLEDGE - emphasize beginner-friendly approach]';
  } else if (capturedData.ai_knowledge_rating >= 7) {
    contextToInject += ' [USER HAS HIGH AI KNOWLEDGE - emphasize advanced modules and projects]';
  }
}

if (capturedData.needs_guidance) {
  contextToInject += ' [USER NEEDS GUIDANCE - emphasize mentorship and structured roadmap]';
}

if (capturedData.long_term_goal_type === 'entrepreneurship') {
  contextToInject += ' [USER WANTS TO START BUSINESS - emphasize monetization and freelancing modules]';
}

// Add urgency context after certain turns
if (currentState.turn_count >= 10 && !capturedData.urgency_mentioned) {
  contextToInject += ' [INJECT URGENCY: Only 5 discounted slots remaining]';
  capturedData.urgency_mentioned = true;
}

// Track previous phase for objection recovery
if (nextPhase && nextPhase.startsWith('objection_')) {
  currentState.data.previous_phase = currentState.phase;
}

// Update state
if (nextPhase) {
  callStates[callId] = { 
