const finalState = callStates[callId] || { phase: 'unknown', data: {}, sentiment: 'neutral', domain: null, turn_count: 0, objections_handled: [] };

// Determine lead type from final state
let leadType = 'cold';
let followupPriority = 'low';

if (finalState.phase === 'closing_hot' || finalState.phase === 'closing_payment') {
  leadType = 'hot';
  followupPriority = 'immediate';
} else if (finalState.phase === 'closing_warm' || finalState.sentiment === 'positive') {
